concepts.example:
#include <memory>

std::unique_ptr<int> ptr = std::make_unique<int>(42);
std::shared_ptr<int> sptr = std::make_shared<int>(100);

class MyClass {
public:
    MyClass(MyClass&& other) noexcept {
        data = std::move(other.data);
    }
private:
    std::vector<int> data;
};

examples.code:
#include <vector>
#include <iostream>

int main() {
    std::vector<int> vec = {1, 2, 3, 4, 5};
    vec.push_back(6);

    for (int num : vec) {
        std::cout << num << " ";
    }
    return 0;
}

class Rectangle {
private:
    int width, height;
public:
    Rectangle(int w, int h) : width(w), height(h) {}

    int area() {
        return width * height;
    }
};
