class Node:
    def __init__(self, data):
        self.data = data
        self.next = None

class LinkedList:
    def __init__(self):
        self.head = None

    def append(self, data):
        new_node = Node(data)
        if not self.head:
            self.head = new_node
            return
        current = self.head
        while current.next:
            current = current.next
        current.next = new_node

class HashMap:
    def __init__(self):
        self.size = 100
        self.map = [None] * self.size

    def _hash(self, key):
        return hash(key) % self.size

    def put(self, key, value):
        index = self._hash(key)
        self.map[index] = (key, value)

    def get(self, key):
        index = self._hash(key)
        return self.map[index][1] if self.map[index] else None
