const numbers = [1, 2, 3, 4, 5, 6];
const result = numbers
    .filter(n => n % 2 === 0)
    .map(n => n * 2)
    .reduce((sum, n) => sum + n, 0);
console.log(result); // 24

class Person {
    constructor(name, age) {
        this.name = name;
        this.age = age;
    }

    greet() {
        return `Hello, I'm ${this.name}`;
    }
}

const person = new Person('Alice', 30);
console.log(person.greet());
